module.exports =  {


    Calcululator(op,value1,value2){
        
    let result;
    if(op == "+"){
        result = value1 + value2;
    }
    else if(op == "-"){
        result = value1 - value2;
    }
    else if(op == "/"){
        result = value1 / value2;
    }
    else if(op == "%"){
        result = value1 % value2;
    }
    return result;
    }
    
};